  method: "POST",
  headers: {
    "Content-Type": "application/json",
    Authorization: `Bearer ${process.env.WORQHAT_API_KEY}`
  },
  body: JSON.stringify({
    table: "customer_management_data",
    data: {
      customer_name: "Alice Johnson",
      customer_email: "alice@example.com"
    }
  })
});
const data = await response.json();
console.log(data);

