const storedEmail = window.localStorage.getItem("wh-playground-email");
const email = storedEmail ?? "support@worqhat.com";
const response = await fetch("https://api.worqhat.com/flows/trigger/86a34e0f-60a9-4219-af5b-47aa7aa7c05b", {
  method: "POST",
  headers: {
    "Content-Type": "application/json",
    Authorization: `Bearer ${process.env.WORQHAT_API_KEY}`
  },
  body: JSON.stringify({
    email
  })
});
const data = await response.json();
console.log(data);

requestSdkCode:
import Worqhat from "worqhat";

const client = new Worqhat({ apiKey: process.env.WORQHAT_API_KEY! });
const storedEmail = window.localStorage.getItem("wh-playground-email");
const email = storedEmail ?? "support@worqhat.com";
const result = await client.flows.triggerWithPayload(
  "86a34e0f-60a9-4219-af5b-47aa7aa7c05b",
  { body: { email } } as any
);
console.log(result);
